			/* header */
			.domestica header {
				position:relative;
				height:74px;
				margin-bottom:35px;
				padding:0;
			}
			
			.domestica header h1 {
				position:absolute;
				right:0;
				bottom:0;
				font-weight:normal;
				margin:0;
				padding:0;
			}
			
			.domestica header #logo {
				position:relative;
				height:75px;
				overflow:hidden;
				width:215px;
				margin:0;
			}
			.domestica header #logo .illustration {
				position:absolute;
				top:0px;
				left:0;
			}
			.domestica header #logo .illustration div {
				width:18px;
				height:18px;
				margin-top:1px;
				border-radius:3px;
			}
			.domestica header #logo .illustration div.first {
				margin-top:0;
			}
			.domestica header #logo .illustration .block-1 {background-color:rgba(118,118,118,1);}
			.domestica header #logo .illustration .block-2 {background-color:rgba(28,28,28,1);}
			.domestica header #logo .illustration .block-3 {background-color:rgba(79,79,79,1);}
			.domestica header #logo .illustration .block-4 {background-color:rgba(125,125,125,1);}
			.domestica header #logo h2 {
				font-size:47px;
				font-weight:500;
				padding:0;
				margin: 0 0 0 28px;
				position:absolute;
				left:0;
				top:-1px;
				line-height:39px;
			}
			
			/* body */
			.domestica section {
				margin-bottom:35px;
			}
			.domestica section article {
				position:relative;
			}
			.domestica section article figure, .domestica section article figcaption{
				display:inline-block;
				vertical-align:bottom;
			}
			.domestica section article figure {
				width:640px;
			}
			
			.domestica section article figure img {
				border-radius:3px;
			}
			
			.domestica section article figcaption{
				width:500px;
				margin-left:20px;
				padding:5px;
				background-color:#efefef;
				border-radius:3px;
				margin-bottom:2px;
			}
			.domestica section article figcaption p:last-child {
				margin-bottom:0;
			}
			.domestica section h2 {
				position:absolute;
				top:0;
				left:660px;
				margin:0;
				padding:0px;
				font-weight:normal;
				display:block;
				width:500px;
				height:70px;
				
			}
			
			/* nav */
			.domestica nav {
				margin-bottom:25px;
			}
			
			.domestica nav ul {
				margin:0;
				margin-right:-2px;
				padding:0;
			}
			
			.domestica nav ul li {
				display:inline-block;
				width:284px;
				height:100px;
				vertical-align:top;
				color:#aaa;
				padding:10px;
				margin:0 0 0 0;
				list-style-type: none;
				border:solid 1px #fff;
				border-radius:3px;
			}
			
			.domestica nav ul li:hover {
				color:#333;
				border:solid 1px #eee;
				cursor:pointer;cursor:hand;
			}
			
			.domestica nav ul li a {
				font-size:24px;
				color:#333;
				font-family:georgia,garamond,serif;
				
			}
			
			.domestica nav ul li:hover a {
				color:blue;
			}
			
			/* footer */
			.domestica footer img {
				float:left;
				
			}
